<template>
    <div class="page">
      <DemoHead name="pull-to-refresh"></DemoHead>
      <div class="button">下拉啊～～</div>
    </div>
</template>

<script>
import DemoHead from "@/common/components/demo-head.vue";
export default {
  name: "pull-to-refresh",
  data() {
    return {};
  },
  computed: {},
  methods: {},
  components: {
    DemoHead
  },
  mounted() {},
  created() {
    window.document.addEventListener(
      "pull-to-refresh-demo",
      function (e) {
        setTimeout(() => {
          e.detail.finish(); // 结束菊花
          alert('～^～');
        }, 1000);
      },
      false
    );
  }
};
</script>

